import React, { useState } from "react";
import { fetchPublicationsFromXml } from "./services/orcidService";
import { exportToCsv } from "./utils/csvExporter";
import { exportToBib } from "./utils/bibExporter";

function App() {
  const [orcidId, setOrcidId] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [publications, setPublications] = useState([]);

  const handleFetchPublications = async () => {
    setLoading(true);
    setError("");

    try {
      const fetchedPublications = await fetchPublicationsFromXml(orcidId);
      if (fetchedPublications.length > 0) {
        setPublications(fetchedPublications);
      } else {
        setError("No publications found for the provided ORCID ID.");
      }
    } catch (err) {
      setError("Error fetching publications. Please check the ORCID ID.");
    } finally {
      setLoading(false);
    }
  };

  const handleExportCsv = () => {
    if (publications.length > 0) {
      exportToCsv(publications, "iris_import.csv");
    }
  };

  const handleExportBib = () => {
    if (publications.length > 0) {
      exportToBib(publications, "publications.bib");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>ORCID to IRIS Exporter</h1>
      <label>
        ORCID ID:
        <input
          type="text"
          value={orcidId}
          onChange={(e) => setOrcidId(e.target.value)}
          placeholder="Enter your ORCID ID"
          style={{ margin: "10px 0", padding: "10px", width: "100%" }}
        />
      </label>
      <br />
      <button
        onClick={handleFetchPublications}
        disabled={loading}
        style={{ padding: "10px 20px", marginTop: "10px" }}
      >
        {loading ? "Fetching..." : "Fetch Publications"}
      </button>
      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      {publications.length > 0 && (
        <>
          <button
            onClick={handleExportCsv}
            style={{ padding: "10px 20px", marginTop: "10px", marginRight: "10px" }}
          >
            Export CSV
          </button>
          <button
            onClick={handleExportBib}
            style={{ padding: "10px 20px", marginTop: "10px" }}
          >
            Export BibTeX
          </button>
        </>
      )}
    </div>
  );
}

export default App;
