import React, { useState } from "react";
import { fetchPublicationsAsXml } from "./services/orcidService";
import { exportToCsv } from "./utils/csvExporter";
import { exportToBibtex } from "./utils/bibtexExporter";

function App() {
  const [orcidId, setOrcidId] = useState("");
  const [publications, setPublications] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const handleFetchPublications = async () => {
    setLoading(true);
    setError("");
    setMessage("");

    try {
      const data = await fetchPublicationsAsXml(orcidId);
      if (data.length > 0) {
        setPublications(data);
        setMessage(`Successfully fetched ${data.length} publications.`);
      } else {
        setMessage("No publications found for the provided ORCID ID.");
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleExportFiles = () => {
    if (publications.length > 0) {
      exportToCsv(publications, "iris_import.csv");
      exportToBibtex(publications, "iris_import.bib");
      setMessage("Files exported successfully.");
    } else {
      setError("No publications available to export.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>ORCID to IRIS Converter</h1>
      <label>
        ORCID ID:
        <input
          type="text"
          value={orcidId}
          onChange={(e) => setOrcidId(e.target.value)}
          placeholder="Enter ORCID ID"
          style={{ margin: "10px 0", padding: "10px", width: "100%" }}
        />
      </label>
      <br />
      <button
        onClick={handleFetchPublications}
        disabled={loading}
        style={{ padding: "10px 20px", marginRight: "10px" }}
      >
        {loading ? "Fetching..." : "Fetch Publications"}
      </button>
      <button
        onClick={handleExportFiles}
        disabled={loading || publications.length === 0}
        style={{ padding: "10px 20px" }}
      >
        Export CSV & BibTeX
      </button>
      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      {message && <p style={{ color: "green", marginTop: "10px" }}>{message}</p>}
      {publications.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h3>Fetched Publications:</h3>
          <ul>
            {publications.map((pub, index) => (
              <li key={index}>
                <strong>{pub.title}</strong> ({pub.year}) - {pub.journal}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
