import { parseStringPromise } from "xml2js";

export async function fetchPublicationsFromXml(orcidId) {
  // Validate ORCID ID format
  if (!orcidId || !/^\d{4}-\d{4}-\d{4}-\d{4}$/.test(orcidId)) {
    throw new Error("Invalid ORCID ID format. Please use the format ####-####-####-####.");
  }

  const url = `https://pub.orcid.org/v3.0/${orcidId}/activities`;
  const headers = { Accept: "application/xml" };

  try {
    // Fetch data from ORCID API
    const response = await fetch(url, { headers });

    if (!response.ok) {
      throw new Error(`Failed to fetch XML: ${response.status} - ${response.statusText}`);
    }

    const xml = await response.text();
    console.log("Fetched XML:", xml); // Debugging

    // Parse XML response to JSON
    const result = await parseStringPromise(xml, { explicitArray: false });
    console.log("Parsed JSON Response:", JSON.stringify(result, null, 2)); // Debugging

    // Navigate to works data
    const works = result?.["activities:activities-summary"]?.["activities:works"]?.["activities:group"];
    if (!works) {
      console.warn("No works found for this ORCID ID.");
      return [];
    }

    // Extract and normalize works data
    const publications = Array.isArray(works)
      ? works.flatMap((workGroup) => parseWorkGroup(workGroup))
      : parseWorkGroup(works);

    console.log("Parsed Publications:", publications); // Debugging
    return publications;
  } catch (error) {
    console.error("Error fetching or parsing XML:", error.message);
    throw new Error("Failed to fetch or parse publications. Please check the ORCID ID.");
  }
}

// Helper function to parse a single work group
function parseWorkGroup(workGroup) {
  const summaries = Array.isArray(workGroup["work:work-summary"])
    ? workGroup["work:work-summary"]
    : [workGroup["work:work-summary"]];

  return summaries.map((summary) => {
    const externalIds = Array.isArray(summary?.["common:external-ids"]?.["common:external-id"])
      ? summary["common:external-ids"]["common:external-id"]
      : [summary?.["common:external-ids"]?.["common:external-id"]].filter(Boolean);

    const doi = externalIds.find((id) => id["common:external-id-type"] === "doi")?.["common:external-id-value"] || "";

    return {
      title: summary?.["work:title"]?.["common:title"] || "No Title",
      authors: "No Authors", // Public ORCID data doesn't include authors
      doi,
      journal: summary?.["work:journal-title"]?.["common:title"] || "No Journal",
      year: summary?.["common:publication-date"]?.["common:year"] || "No Year",
      volume: summary?.["work:volume"] || "No Volume",
      issue: summary?.["work:issue"] || "No Issue",
      pages: summary?.["work:page-range"] || "No Pages",
      abstract: summary?.["common:short-description"] || "No Abstract",
      publisher: summary?.["work:publisher"]?.["common:title"] || "No Publisher",
    };
  });
}
