export async function fetchPublications(orcidId) {
    const url = `https://pub.orcid.org/v3.0/${orcidId}/works`;

    const response = await fetch(url, {
      headers: {
        Accept: "application/json",
      },
    });

    if (!response.ok) {
      throw new Error("Failed to fetch publications");
    }

    const data = await response.json();
    const publications = [];

    for (const work of data.group || []) {
      const summary = work["work-summary"][0];
      const title = summary.title?.title?.value || "Unknown Title";
      let doi = null;

      for (const extId of summary["external-ids"]?.["external-id"] || []) {
        if (extId["external-id-type"] === "doi") {
          doi = extId["external-id-value"];
          break;
        }
      }

      publications.push({
        title,
        authors: "Unknown Authors", // ORCID does not provide author info in public API
        doi,
        journal: summary["journal-title"]?.value || "Unknown Journal",
        year: summary["publication-date"]?.year?.value || "Unknown Year",
        volume: "Unknown Volume", // Replace if data is available
        issue: "Unknown Issue", // Replace if data is available
        pages: "Unknown Pages", // Replace if data is available
        abstract: "No abstract available", // Replace if data is available
      });
    }

    return publications;
  }
