export function exportToBibtex(data, filename) {
    const bibtexContent = data
      .map((item, index) => {
        const authors = item.authors
          .split(", ")
          .map((author) => author.replace(/ /g, "_"))
          .join(" and ");

        return `@article{pub${index + 1},
    title = {${item.title}},
    author = {${authors}},
    journal = {${item.journal || "Unknown Journal"}},
    year = {${item.year || "Unknown Year"}},
    volume = {${item.volume || "Unknown Volume"}},
    number = {${item.issue || "Unknown Issue"}},
    pages = {${item.pages || "Unknown Pages"}},
    doi = {${item.doi || ""}},
    abstract = {${item.abstract || ""}},
    publisher = {${item.publisher || "Unknown Publisher"}},
    keywords = {${item.keywords || ""}}
  }`;
      })
      .join("\n\n");

    const blob = new Blob([bibtexContent], { type: "text/plain;charset=utf-8;" });
    const link = document.createElement("a");
    const url = URL.createObjectURL(blob);
    link.setAttribute("href", url);
    link.setAttribute("download", filename);
    link.style.visibility = "hidden";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  }
