import React, { useState } from "react";
import { fetchPublicationsWithToken } from "./services/orcidService";
import { exportToCsv } from "./utils/csvExporter";
import { exportToBib } from "./utils/bibExporter";
import LoginButton from "./components/LoginButton";

function App() {
  const [orcidId, setOrcidId] = useState("");
  const [publications, setPublications] = useState([]);
  const token = localStorage.getItem("orcid_token");

  const handleFetch = async () => {
    try {
      const pubs = await fetchPublicationsWithToken(orcidId, token);
      setPublications(pubs);
    } catch (error) {
      console.error("Error fetching publications:", error.message);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>ORCID to CSV/BibTeX Exporter</h1>
      {!token && <LoginButton />}
      {token && (
        <>
          <input
            type="text"
            value={orcidId}
            onChange={(e) => setOrcidId(e.target.value)}
            placeholder="Enter ORCID ID"
            style={{ margin: "10px 0", padding: "10px", width: "100%" }}
          />
          <button onClick={handleFetch} style={{ padding: "10px 20px" }}>
            Fetch Publications
          </button>
          <br />
          <button
            onClick={() => exportToCsv(publications)}
            style={{ padding: "10px 20px", marginTop: "10px", marginRight: "10px" }}
          >
            Export CSV
          </button>
          <button
            onClick={() => exportToBib(publications)}
            style={{ padding: "10px 20px", marginTop: "10px" }}
          >
            Export BibTeX
          </button>
        </>
      )}
    </div>
  );
}

export default App;
